def find_maximum(numbers: list) -> int:
    """Finds the maximum value in a list of numbers."""
    if not numbers:  # Handle edge case of empty input list
        return None

    max_number = numbers[0]
    for num in numbers:
        if num > max_number:
            max_number = num

    return max_number

def main():
    numbers_str = input("Enter a list of numbers, separated by spaces: ").split()
    try:
        numbers = [int(num) for num in numbers_str]  # Convert to integers if they are not
    except ValueError:
        print("Invalid input. Please enter only numbers.")
        return

    max_number = find_maximum(numbers)
    if max_number is None:
        print("No maximum number found in the list.")
    else:
        print(f"The maximum number is {max_number}")

if __name__ == "__main__":
    # Testing find_maximum function
    assert find_maximum([3, 1, 4, 1, 5]) == 5
    assert find_maximum([]) == None

    main()  # Interactive mode
