def is_prime(n: int) -> bool:
    """Check if a number is prime."""
    if n <= 1:
        return False
    elif n == 2:
        return True
    else:
        for i in range(2, int(n**0.5) + 1):
            if n % i == 0:
                return False
        return True

def main():
    while True:
        n = int(input("Enter a number: "))
