def is_prime(n: int) -> bool:
    """Check if a number is prime."""
    if n <= 1:
        return False
    elif n == 2:
        return True
    else:
        for i in range(2, int(n**0.5) + 1):
            if n % i == 0:
                return False
        return True

def main():
    n = int(input("Enter a number: "))
    result = is_prime(n)
    print(f"Is {n} prime? {result}")

if __name__ == "__main__":
    # Test the function
    assert is_prime(2) == True, "Test case 1 failed"
    assert is_prime(4) == False, "Test case 2 failed"
    assert is_prime(37) == True, "Test case 3 failed"
    assert is_prime(40) == False, "Test case 4 failed"

